mapper.select:
select

  distinct


from u_user

  


  order by ${orderByClause}


  limit ${start} , ${limit}

    SELECT COUNT(DISTINCT(u.id))
	FROM u_user u
		LEFT JOIN s_user_group ug ON u.user_code = ug.user_code
		LEFT JOIN s_group sg  ON ug.group_code = sg.group_code
	WHERE 1=1 
	  
        and u.user_code = #{userCode,jdbcType=VARCHAR}
      
      
        and user_name like concat('%',#{userName,jdbcType=VARCHAR},'%')   
      
      
        and dict_status = #{dictStatus,jdbcType=TINYINT}
      
      
        and site_code = #{siteCode,jdbcType=VARCHAR}
      
      
        and dict_dept = #{dictDept,jdbcType=TINYINT}
      
      
        and dict_position = #{dictPosition,jdbcType=TINYINT}
      
      
        and dict_is_enable_right = #{dictIsEnableRight,jdbcType=INTEGER}
      
      
        and ug.group_code = #{groupCode,jdbcType=VARCHAR}

SELECT u.id AS id, u.user_code AS userCode, u.user_name AS userName, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_is_emp) AS isEmp, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_sex) AS sex, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_pos_status) AS posStatus, u.site_code AS siteCode, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_dept) AS dept, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_position) AS pos, GROUP_CONCAT(sg.group_name) AS groupName, u.create_operator AS createOperator, u.create_time AS createTime, u.update_operator AS updateOperator, u.update_time AS updateTime, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_is_enable_right) AS isEnableRight FROM u_user u LEFT JOIN s_user_group ug ON u.user_code = ug.user_code LEFT JOIN s_group sg ON ug.group_code = sg.group_code WHERE 1=1
and u.user_code = #{userCode,jdbcType=VARCHAR}
and u.user_name like concat('%',#{userName,jdbcType=VARCHAR},'%')
and u.dict_pos_status = #{dictStatus,jdbcType=VARCHAR}
and u.site_code = #{siteCode,jdbcType=VARCHAR}
and u.dict_dept = #{dictDept,jdbcType=VARCHAR}
and u.dict_position = #{dictPosition,jdbcType=VARCHAR}
and u.dict_is_enable_right = #{dictIsEnableRight,jdbcType=VARCHAR}
and ug.group_code = #{groupCode,jdbcType=VARCHAR}
GROUP BY u.id

SELECT u.id AS id, u.user_code AS userCode, u.user_name AS userName, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_is_emp) AS isEmp, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_sex) AS sex, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_pos_status) AS posStatus, u.site_code AS siteCode, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_dept) AS dept, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_position) AS pos, GROUP_CONCAT(sg.group_name) AS groupName, u.create_operator AS createOperator, u.create_time AS createTime, u.update_operator AS updateOperator, u.update_time AS updateTime, (SELECT item_value FROM `s_dict_item` WHERE item_code = u.dict_is_enable_right) AS isEnableRight FROM u_user u LEFT JOIN s_user_group ug ON u.user_code = ug.user_code LEFT JOIN s_group sg ON ug.group_code = sg.group_code WHERE 1=1
and u.user_code = #{userCode,jdbcType=VARCHAR}
and u.user_name like concat('%',#{userName,jdbcType=VARCHAR},'%')
and u.dict_pos_status = #{dictStatus,jdbcType=VARCHAR}
and u.site_code = #{siteCode,jdbcType=VARCHAR}
and u.dict_dept = #{dictDept,jdbcType=VARCHAR}
and u.dict_position = #{dictPosition,jdbcType=VARCHAR}
and u.dict_is_enable_right = #{dictIsEnableRight,jdbcType=VARCHAR}
and ug.group_code = #{groupCode,jdbcType=VARCHAR}
GROUP BY u.id
